        var userChangeLocation = (function(params) {
            var geoPlaces = {};
            const getGeoPlaces = () => geoPlaces;
            const setGeoPlaces = (_geoPlaces) => {
                geoPlaces = _geoPlaces;
                return true;
            }
            const fetchLocation = (data, useFormattedAddress) => {
                let geoPlaces = {};
                data.address_components.forEach(function(value, index, arr) {
                    if ($.inArray('route', value.types) > -1) {
                        geoPlaces.route = value.long_name;
                    } else if ($.inArray('sublocality_level_1', value.types) > -1) {
                        geoPlaces.subloca = value.long_name;
                    } else if ($.inArray('locality', value.types) > -1) {
                        geoPlaces.city = value.long_name;
                    } else if ($.inArray('postal_town', value.types) > -1) {
                        geoPlaces.town = value.long_name;
                    } else if ($.inArray('administrative_area_level_1', value.types) > -1) {
                        geoPlaces.state = value.long_name;
                        geoPlaces.stateshort = value.short_name;
                    } else if ($.inArray('country', value.types) > -1) {
                        geoPlaces.country = value.long_name;
                        geoPlaces.countryCode = value.short_name;
                    }
                });
                geoPlaces.lat = data.geometry.location.lat();
                geoPlaces.lng = data.geometry.location.lng();
                geoPlaces.latlng = geoPlaces.lat + ',' + geoPlaces.lng;
                if (!geoPlaces.city && geoPlaces.town) {
                    geoPlaces.city = geoPlaces.town;
                }
                if (!geoPlaces.city && geoPlaces.subloca) {
                    geoPlaces.city = geoPlaces.subloca;
                }
                if (!geoPlaces.city && geoPlaces.state) {
                    geoPlaces.city = geoPlaces.state;
                }
                let shortAddress = '';
                let shortAddressNames = geoPlaces.city && geoPlaces.subloca && geoPlaces.city != geoPlaces.subloca ? ['subloca', 'city'] : ['city'];
                if (geoPlaces.city && geoPlaces.stateshort && geoPlaces.city != geoPlaces.stateshort) {
                    shortAddressNames.push('stateshort');
                }
                shortAddressNames.push('country');
                var shortAddressArr = [];
                shortAddressNames.forEach(function(name) {
                    for (const key in geoPlaces) {
                        if (key == name && geoPlaces[key] != '') {
                            shortAddressArr.push(geoPlaces[key]);
                        }
                    }
                });
                shortAddress = shortAddressArr.join(', ').toString();
                geoPlaces.address = useFormattedAddress ? data.formatted_address : shortAddress;
                return geoPlaces;
            };
            const processGeoData = (data) => {
                let geoPlaces = data;
                if (typeof geoPlaces.lat !== 'string' && typeof geoPlaces.lat !== 'number') geoPlaces.lat = '';
                if (typeof geoPlaces.lng !== 'string' && typeof geoPlaces.lat !== 'number') geoPlaces.lng = '';
                if (typeof geoPlaces.address !== 'string') geoPlaces.address = '';
                if (typeof geoPlaces.country !== 'string') geoPlaces.country = '';
                if (typeof geoPlaces.countryCode !== 'string') geoPlaces.countryCode = '';
                if (typeof geoPlaces.city !== 'string') geoPlaces.city = '';
                return {
                    lat_F: geoPlaces.lat,
                    lng_F: geoPlaces.lng,
                    address_F: geoPlaces.address,
                    country_F: geoPlaces.country,
                    country_code_F: geoPlaces.countryCode,
                    city_F: geoPlaces.city,
                };
            }
            const updateLocationAttrs = (data) => {
                let geoPlaces = data;
                const latEl = document.querySelector('.search-location-lat');
                const lonEl = document.querySelector('.search-location-lon');
                const addEl = document.querySelector('.search-location-address');
                latEl && latEl.setAttribute('value', geoPlaces.lat !== null && geoPlaces.lat !== undefined ? geoPlaces.lat : '');
                lonEl && lonEl.setAttribute('value', geoPlaces.lng !== null && geoPlaces.lng !== undefined ? geoPlaces.lng : '');
                addEl && addEl.setAttribute('value', geoPlaces.address !== null && geoPlaces.address !== undefined ? geoPlaces.address : '');
                let searchInput = document.querySelector('.pac-target-input');
                if (!!searchInput) {
                    searchInput.value = geoPlaces.address !== null && geoPlaces.address !== undefined ? geoPlaces.address : '';
                    searchInput.setAttribute('data-location', JSON.stringify(geoPlaces))
                }
            }
            const updateLocation = (geoPlaces) => {
                let geoData = {
                    lat_F: '',
                    lng_F: '',
                    address_F: '',
                    country_F: '',
                    country_code_F: '',
                    city_F: ''
                };
                if (typeof geoPlaces != 'undefined' && geoPlaces != null) {
                    geoData = processGeoData(geoPlaces);
                }
                return new Promise((resolve, reject) => {
                    $.ajax({
                        type: 'post',
                        url: '/ajax/ng/geo/change-location',
                        data: geoData,
                        success: function(data) {
                            resolve(data);
                        },
                        error: function(error) {
                            reject(error);
                        },
                    });
                })
            }
            const doUpdate = (data, reload = false) => {
                if ($('#modalChangeLocation').length > 0) {
                    $('#modalChangeLocation').modal('hide');
                }
